import React, { useState, useEffect } from "react";

function DestinationAdmin({ user }) {
  const [destinations, setDestinations] = useState([]);
  const [newDestination, setNewDestination] = useState("");

  useEffect(() => {
    fetch("/destinations")
      .then((r) => r.json())
      .then(setDestinations);
  }, []);

  const handleCreate = () => {
    fetch("/destinations", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name: newDestination }),
    })
      .then((r) => r.json())
      .then((newDest) => setDestinations([...destinations, newDest]));
  };

  const handleDelete = (id) => {
    fetch(`/destinations/${id}`, {
      method: "DELETE",
    }).then(() => {
      setDestinations(destinations.filter((dest) => dest.id !== id));
    });
  };

  return (
    <>
    <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg mt-32">
      <h1 className="text-2xl font-bold mb-4">Welcome {user.username}</h1>
      <h2 className="text-2xl font-bold mb-4">Admin: Manage Destinations</h2>

      <div className="mb-4">
        <input
          type="text"
          value={newDestination}
          onChange={(e) => setNewDestination(e.target.value)}
          placeholder="New destination"
          className="p-2 border border-gray-300 rounded-lg w-full max-w-md"
        />
        <button
          onClick={handleCreate}
          className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600"
        >
          Create Destination
        </button>
      </div>
    </div>

    <div>
    {destinations.map((dest) => (
        <li
          key={dest.id}
          className="flex items-center justify-between p-4 border border-gray-200 rounded-lg"
        >
          <span className="text-lg font-semibold">{dest.name}</span>
          <button
            onClick={() => handleDelete(dest.id)}
            className="bg-red-500 text-white py-1 px-3 rounded-lg hover:bg-red-600"
          >
            Delete
          </button>
        </li>
      ))}
    </div>
    </>


  );
}

export default DestinationAdmin;
