import React, { useState } from "react";
import Destination from "./Destination";

function DestinationList({ destinations }) {
  const [isOpen, setIsOpen] = useState(false);
  const [groupName, setGroupName] = useState("All Destinations");

  const toggleItems = (destination) => {
    setIsOpen(!isOpen);
    setGroupName(isOpen ? "All Destinations" : destination.category);
  };

  return (
    <div>
      <div className="border p-4 mb-4 mt-16">
        <div
          className="cursor-pointer text-lg font-bold bg-gray-200 p-2 rounded"
          onClick={toggleItems}
        >
          {groupName}
        </div>
      </div>
      <h1 className="text-3xl text-gray-800 font-bold truncate block capitalize mb-4 mt-16">
        Bucket List Destinations
      </h1>
      {isOpen
        ? destinations.map((destination) => {
            if (destination.id === 1) {
              return (
                <Destination key={destination.id} destination={destination} />
              );
            }
          })
        : null}
    </div>
  );
}

export default DestinationList;
