import React, { useEffect, useState } from "react";
import {  Routes, Route } from "react-router-dom";
import NavBar from "./NavBar";
import Login from "../pages/Login";
import Signup from "../pages/Signup";
import HomePageLoggedIn from "../pages/HomePageLoggedIn";
import HomePageNotLoggedIn from "../pages/HomePageNotLoggedIn";


function App() {
  const [user, setUser] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    fetch('/check_session').then((r) => {
      if (r.ok) {
        r.json().then((user) => setUser(user));
      }
    });
  }, []);

  return (
    <>

    <main className={isDarkMode ? "bg-white dark:bg-gray-800 text-black dark:text-white" : ""}>
    <NavBar isDarkMode={isDarkMode} onToggleDarkMode={setIsDarkMode} user={user} setUser={setUser}/>
        {user ? (
          <HomePageLoggedIn user={user} setUser={setUser} />
        ) : (
          <Routes>
            <Route
              path="/signup"
              element={<Signup setUser={setUser} />}
            />
            <Route
              path="/login"
              element={<Login setUser={setUser} />}
            />
            <Route path="/" element={<HomePageNotLoggedIn />} />
          </Routes>
        )}
      </main>
    </>

  );
}

export default App;
