import React from "react";

function BookedDestination({ booked_dest, onRe }) {
  const { id, name, description, image, category, location, reviews, price } =
    booked_dest; 


  const handleRemove = (destination) => {
    const updatedBookedDestinations = booked.filter((dest) => dest.id!== destination.id);
    setBookedDestinations(updatedBookedDestinations);  // Call the parent component's function to update the state with the new array of booked destinations.  This will trigger the parent component to re-render.  
                                                   // Note: The parent component must have a prop 'booked' and 'setBooked' to pass the state and function to this component. 
                                                   // Also, the parent component must have a state called 'booked' to hold the current array of booked destinations.  
                                                   // This component is a child component and cannot directly update the parent component's state.  It only communicates with the parent component through props.  
                                                   // In this case, the parent component is YourDestinations.js, and it updates the state there. 
                                                   // This ensures that the state in the parent component is always up

  };

  return (
    <div className="p-4 border-b border-gray-200 mt-8">
      <div className="flex items-center gap-4">
        <img
          className="w-20 h-20 rounded-full object-cover border-4 border-green-500"
          src={image}
          alt={name}
        />
        <div>
          <h2 className="text-lg font-semibold text-gray-800">{name}</h2>
          <p className="text-sm text-gray-600">{location}</p>
        </div>
      </div>
      <div className="flex justify-between items-center mt-4">
        <span className="text-sm font-medium text-gray-700">
          Price: ${price}
        </span>
        <button className="text-sm font-semibold text-green-600 hover:text-green-800"
        onClick = {() => handleRemove(destination)}>
          Remove
        </button>
      </div>
    </div>
  );
}

export default BookedDestination;
