import React, { useState } from 'react'
import Destination from './Destination'


function DestinationList({ destinations }) {
  const [showBeach, setShowBeach] = useState(false)

  const toggleBeachList = () => {
    setShowBeach(!showBeach);
  };

  return (
    <div >
      <h1 className='text-3xl text-gray-800 font-bold truncate block capitalize mb-4 mt-16'>Bucket List Destinations</h1>
      <div
  className="cursor-pointer text-xl font-semibold bg-white dark:bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200 transform hover:scale-105 text-green-700 border border-gray-300 dark:border-gray-700"
  onClick={toggleBeachList}
>
  Beach
</div>

      {showBeach ? destinations.map((destination) => {
        if (destination.category === 'Beach'){
          return (
            <Destination
            key={destination.id}
            destination={destination} />
          )
        }
      }
      ): null}
    </div>
  )
}

export default DestinationList
