import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar as fas } from '@fortawesome/free-solid-svg-icons';
import { faStar as far } from '@fortawesome/free-regular-svg-icons';


// Custom component to display star ratings
function StarRating({ rating }) {
  const stars = Array.from({ length: 5 }, (_, i) => i + 1);

  return (
    <div className="flex items-center space-x-1">
      {stars.map((star) => (
        <span key={star} className="text-yellow-500 text-2xl">
          <FontAwesomeIcon icon={star <= rating ? fas : far } />
        </span>
      ))}
    </div>
  );
}









function Destination({ destination }) {
  const { id, name, description, image, category, location, reviews, price } =
    destination;
    rating = reviews.
  return (
    <Link to={`/destinations/${id}`}>
      <div className="flex flex-col md:flex-row max-w-4xl rounded-lg shadow-lg bg-white mx-auto my-8 border border-gray-200 p-6">
        <img
          className="w-full md:w-1/2 h-64 md:h-auto object-cover rounded-lg mb-4 md:mb-0 md:mr-6"
          src={image}
          alt={name}
        />
        <div className="flex flex-col justify-between">
          <div>
            <h2 className="text-3xl font-semibold text-gray-800 truncate capitalize mb-4">
              {name}
            </h2>
            <div>
              <span className="text-gray-500 text-sm mr-2">
                {/* <FontAwesomeIcon icon={['fas', 'star']} />
                {(reviews[0].rating)} stars */}
                {StarRating((2))} stars
              </span>
              {reviews.length} reviews


            </div>
            <p className="text-lg text-gray-600 mb-2">{location}</p>
            <p className="text-gray-700 text-base mb-4">{description}</p>
            <p className="text-gray-500 text-sm mb-4">Category: {category}</p>
            <p className="text-gray-500 text-sm mb-4">Price: {price}</p>
          </div>
          <div>
            <p className="text-gray-700 font-semibold mb-2">Reviews:</p>
            <div className="text-gray-600 text-sm mb-4">
              {reviews[0].comment}
            </div>
            <hr className="border-gray-300 my-4" />
            <button
              type="button"
              className="w-full md:w-32 bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition-colors shadow-sm"
            >
              Book now
            </button>
          </div>
        </div>
      </div>
    </Link>
  );
}

export default Destination;
