import React, { useState, useEffect } from "react";

function DestinationAdmin() {
  const [destinations, setDestinations] = useState([]);
  const [newDestination, setNewDestination] = useState("");

  useEffect(() => {
    fetch("/destinations")
      .then((r) => r.json())
      .then(setDestinations);
  }, []);

  const handleCreate = () => {
    fetch("/destinations", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name: newDestination }),
    })
      .then((r) => r.json())
      .then((newDest) => setDestinations([...destinations, newDest]));
  };

  const handleDelete = (id) => {
    fetch(`/destinations/${id}`, {
      method: "DELETE",
    }).then(() => {
      setDestinations(destinations.filter((dest) => dest.id !== id));
    });
  };

  return (
    <div>
      <h2>Admin: Manage Destinations</h2>
      <input
        type="text"
        value={newDestination}
        onChange={(e) => setNewDestination(e.target.value)}
        placeholder="New destination"
      />
      <button onClick={handleCreate}>Create Destination</button>

      <ul>
        {destinations.map((dest) => (
          <li key={dest.id}>
            {dest.name}
            <button onClick={() => handleDelete(dest.id)}>Delete</button>
            {/* You can add update functionality here */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DestinationAdmin;
