import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function DestinationDetail() {
  const [destination, setDestination] = useState([]); 
  const { id } = useParams(); // Get the 'id' from the URL parameters

  useEffect(() => {
    fetch(`/destinations/${id}`)
      .then((r) => {
        if (r.ok) {
          return r.json();
        }
        throw new Error("Failed to fetch destination");
      })
      .then((data) => setDestination(data))
      .catch((error) => console.error(error));
  }, [id]);

  if (!destination) {
    return <div>Loading...</div>; // Optional: Loading state while fetching data
  }

  return (
    <div className="max-w-4xl rounded-lg shadow-lg bg-white text-left mx-auto p-6 my-8 border border-gray-200 flex flex-col items-center justify-center max-h-screen">
      <h1 className="text-3xl font-semibold text-gray-800 truncate block capitalize mb-4">{destination.name}</h1>
      <img
        className="w-full h-96 object-cover rounded-lg mb-4"
        src={destination.image}
        alt={destination.name}
      />
      <p>{destination.description}</p>
    </div>
  );
}

export default DestinationDetail;
