import React, { useState } from "react";
import { useParams } from "react-router-dom";

const EditDestination = ({ onUpdateDestination }) => {
  const [destination, setDestination] = useState({
    name: '',
    image: '',
    description: '',
    location: '',
    category: '',
    rating: 0,
    price: 0,
  });
  const [error, setError] = useState('');
  const { id } = useParams();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`/admin/destinations/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(destination),
      });

      if (!response.ok) {
        throw new Error('Failed to update destination');
      }

      const updatedDestination = await response.json();
      onUpdateDestination(updatedDestination);
      alert('Destination updated successfully!');
    } catch (error) {
      setError('Failed to update destination');
      console.error(error);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-32">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Edit Destination</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Name Field */}
        <div className="flex flex-col">
          <label htmlFor="name" className="text-sm font-semibold text-gray-600 mb-1">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={destination.name}
            onChange={(e) => setDestination({ ...destination, name: e.target.value })}
            className="border rounded-md px-3 py-2 focus:ring-2 focus:ring-green-500"
          />
        </div>

        {/* Image URL Field */}
        <div className="flex flex-col">
          <label htmlFor="image" className="text-sm font-semibold text-gray-600 mb-1">Image URL</label>
          <input
            type="text"
            id="image"
            name="image"
            value={destination.image}
            onChange={(e) => setDestination({ ...destination, image: e.target.value })}
            className="border rounded-md px-3 py-2 focus:ring-2 focus:ring-green-500"
          />
        </div>

        {/* Description Field */}
        <div className="flex flex-col">
          <label htmlFor="description" className="text-sm font-semibold text-gray-600 mb-1">Description</label>
          <textarea
            id="description"
            name="description"
            value={destination.description}
            onChange={(e) => setDestination({ ...destination, description: e.target.value })}
            className="border rounded-md px-3 py-2 focus:ring-2 focus:ring-green-500"
            rows="4"
          />
        </div>

        {/* Location Field */}
        <div className="flex flex-col">
          <label htmlFor="location" className="text-sm font-semibold text-gray-600 mb-1">Location</label>
          <input
            type="text"
            id="location"
            name="location"
            value={destination.location}
            onChange={(e) => setDestination({ ...destination, location: e.target.value })}
            className="border rounded-md px-3 py-2 focus:ring-2 focus:ring-green-500"
          />
        </div>

        {/* Category Field */}
        <div className="flex flex-col">
          <label htmlFor="category" className="text-sm font-semibold text-gray-600 mb-1">Category</label>
          <input
            type="text"
            id="category"
            name="category"
            value={destination.category}
            onChange={(e) => setDestination({ ...destination, category: e.target.value })}
            className="border rounded-md px-3 py-2 focus:ring-2 focus:ring-green-500"
          />
        </div>

        {/* Rating Field */}
        <div className="flex flex-col">
          <label htmlFor="rating" className="text-sm font-semibold text-gray-600 mb-1">Rating</label>
          <input
            type="number"
            id="rating"
            name="rating"
            value={destination.rating}
            onChange={(e) => setDestination({ ...destination, rating: parseFloat(e.target.value) })}
            className="border rounded-md px-3 py-2 focus:ring-2 focus:ring-green-500"
            min="0"
            max="5"
          />
        </div>

        {/* Price Field */}
        <div className="flex flex-col">
          <label htmlFor="price" className="text-sm font-semibold text-gray-600 mb-1">Price</label>
          <input
            type="number"
            id="price"
            name="price"
            value={destination.price}
            onChange={(e) => setDestination({ ...destination, price: parseFloat(e.target.value) })}
            className="border rounded-md px-3 py-2 focus:ring-2 focus:ring-green-500"
            min="0"
          />
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="w-full bg-green-600 text-white font-semibold rounded-md px-4 py-2 mt-4 hover:bg-green-700 transition-colors"
        >
          Update Destination
        </button>
      </form>
    </div>
  );
};

export default EditDestination;
