import React from "react";

function Search({ onSearchChange, searchTerm }) {
  return (
    <div>
      <div className="bg-white shadow-md py-2 -mt-28">
        <div className="searchbar flex flex-col items-center my-4 w-full max-w-3xl mx-auto">
          <label
            htmlFor="search"
            className="text-gray-700 font-semibold text-lg mb-2"
          >
            Search Destination:
          </label>
          <input
            type="text"
            id="search"
            placeholder="Type a destination to search..."
            value={searchTerm}
            onChange={(e) => onSearchChange(e.target.value)}
            className="w-full max-w-lg p-3 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 hover"
          />
        </div>
      </div>
    </div>
  );
}

export default Search;
