import React from 'react'
import Destination from './Destination'


function DestinationList({ destinations }) {
  const [showBeach, setShowBeach] = useState(false)

  const toggleBeachList = () => {
    setShowBeach(!showBeach);
  };

  return (
    <div >
      <h1 className='text-3xl text-gray-800 font-bold truncate block capitalize mb-4 mt-16'>Bucket List Destinations</h1>
      <div
        className="cursor-pointer text-lg font-bold bg-gray-200 p-2 rounded"
        onClick={toggleBeachList}
      >
      </div>
      {showdestinations.map((destination) => {
        if (destination.category === 'Beach'){
          return (
            <Destination
            key={destination.id}
            destination={destination} />
          )
        }
      }
      )}
    </div>
  )
}

export default DestinationList
